                      <Button variant="outline" className="w-full gap-2" data-testid={`button-manage-tokens-${asset.id}`}>
                        <Coins className="h-4 w-4" />
                        Tokens
                      </Button>
                    </Link>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}

