import { useQuery, useMutation } from "@tanstack/react-query";
import { Bell, Check, CheckCheck, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";
import type { Notification } from "@shared/schema";

interface NotificationWithMeta extends Notification {
  isNew?: boolean;
}

export function NotificationsDropdown() {
  const { data: notifications = [], isLoading } = useQuery<NotificationWithMeta[]>({
    queryKey: ["/api/notifications"],
    refetchInterval: 30000,
  });

  const { data: unreadData } = useQuery<{ count: number }>({
    queryKey: ["/api/notifications/unread-count"],
    refetchInterval: 30000,
  });

  const markReadMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("PATCH", `/api/notifications/${id}/read`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const markAllReadMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/notifications/read-all");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const unreadCount = unreadData?.count || 0;
  const recentNotifications = notifications.slice(0, 10);

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "KYC_STATUS":
        return "text-blue-500 dark:text-blue-400";
      case "ORDER_FILLED":
        return "text-green-500 dark:text-green-400";
      case "TRANSFER":
        return "text-purple-500 dark:text-purple-400";
      case "ACCOUNT_STATUS":
        return "text-orange-500 dark:text-orange-400";
      case "TOKEN_REVOKED":
        return "text-red-500 dark:text-red-400";
      default:
        return "text-muted-foreground";
    }
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" size="icon" className="relative" data-testid="button-notifications">
          <Bell className="h-5 w-5" />
          {unreadCount > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs"
              data-testid="badge-notification-count"
            >
              {unreadCount > 9 ? "9+" : unreadCount}
            </Badge>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-80">
        <DropdownMenuLabel className="flex items-center justify-between">
          <span>Notifications</span>
          {unreadCount > 0 && (
            <Button 
              variant="ghost" 
              size="sm" 
              className="h-6 text-xs"
              onClick={() => markAllReadMutation.mutate()}
              disabled={markAllReadMutation.isPending}
              data-testid="button-mark-all-read"
            >
              {markAllReadMutation.isPending ? (
                <Loader2 className="h-3 w-3 animate-spin" />
              ) : (
                <>
                  <CheckCheck className="h-3 w-3 mr-1" />
                  Mark all read
                </>
              )}
            </Button>
          )}
        </DropdownMenuLabel>
        <DropdownMenuSeparator />

        {isLoading ? (
          <div className="flex items-center justify-center py-4">
            <Loader2 className="h-5 w-5 animate-spin text-muted-foreground" />
          </div>
        ) : recentNotifications.length === 0 ? (
          <div className="py-6 text-center text-sm text-muted-foreground">
            No notifications yet
          </div>
        ) : (
          <ScrollArea className="h-[300px]">
            {recentNotifications.map((notification) => (
              <DropdownMenuItem
                key={notification.id}
                className={`flex flex-col items-start gap-1 p-3 cursor-pointer ${
                  !notification.read ? "bg-muted/50" : ""
                }`}
                onClick={() => {
                  if (!notification.read) {
                    markReadMutation.mutate(notification.id);
                  }
                }}
                data-testid={`notification-item-${notification.id}`}
              >
                <div className="flex items-center justify-between w-full gap-2">
                  <span className={`font-medium text-sm ${getNotificationIcon(notification.type)}`}>
                    {notification.title}
                  </span>
                  {!notification.read && (
                    <div className="h-2 w-2 rounded-full bg-primary flex-shrink-0" />
                  )}
                </div>
                <p className="text-xs text-muted-foreground line-clamp-2">
                  {notification.message}
                </p>
                <span className="text-xs text-muted-foreground/70">
                  {formatDistanceToNow(new Date(notification.createdAt), { addSuffix: true })}
                </span>
              </DropdownMenuItem>
            ))}
          </ScrollArea>
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/pages/admin-reports.tsx.content:
import { Download, FileSpreadsheet, FileCheck, ClipboardList } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";

export default function AdminReportsPage() {
  const downloadReport = (endpoint: string) => {
    const token = localStorage.getItem("auth_token");

    fetch(endpoint, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to download report");
        const disposition = res.headers.get("Content-Disposition");
        const filenameMatch = disposition?.match(/filename="(.+)"/);
        const filename = filenameMatch ? filenameMatch[1] : "report.csv";
        return res.blob().then((blob) => ({ blob, filename }));
      })
      .then(({ blob, filename }) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        a.remove();
        window.URL.revokeObjectURL(url);
      })
      .catch((err) => {
        console.error("Download failed:", err);
      });
  };

  const reports = [
    {
      id: "compliance",
      title: "Compliance Report",
      description: "Comprehensive platform compliance summary including user statistics, token distribution, and activity metrics",
      icon: FileCheck,
      endpoint: "/api/admin/reports/compliance",
    },
    {
      id: "transactions",
      title: "Transaction History",
      description: "Export all platform transactions including mints, transfers, and trades",
      icon: FileSpreadsheet,
      endpoint: "/api/admin/reports/transactions",
    },
  ];

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-semibold tracking-tight">Admin Reports</h1>
        <p className="text-muted-foreground mt-1">
          Download platform-wide reports for compliance and audit purposes
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {reports.map((report) => (
          <Card key={report.id}>
            <CardHeader>
              <div className="flex items-center gap-3">
                <div className="rounded-md bg-muted p-2">
                  <report.icon className="h-5 w-5 text-muted-foreground" />
                </div>
                <CardTitle className="text-lg">{report.title}</CardTitle>
              </div>
              <CardDescription>{report.description}</CardDescription>
            </CardHeader>
            <CardContent>
              <Button
                onClick={() => downloadReport(report.endpoint)}
                className="w-full gap-2"
                data-testid={`button-download-${report.id}`}
              >
                <Download className="h-4 w-4" />
                Download Report
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

client/src/pages/my-tokens.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import {
  Building2,
  Wheat,
  FileText,
  ArrowLeft,
  TrendingUp,
  TrendingDown,
  Minus,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { AssetTypeBadge } from "@/components/status-badge";
import { Skeleton } from "@/components/ui/skeleton";
import type { Token, Asset, NavHistory } from "@shared/schema";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { format } from "date-fns";

type TokenWithAsset = Token & { asset: Asset };

const assetIcons = {
  real_estate: Building2,
  commodity: Wheat,
  loan: FileText,
};

function TokenHistoryChart({ assetId }: { assetId: string }) {
  const { data: history, isLoading } = useQuery<NavHistory[]>({
    queryKey: [`/api/analytics/asset/${assetId}/nav-history`],
  });

  if (isLoading) {
    return <Skeleton className="h-32 w-full" />;
  }

  if (!history || history.length === 0) {
    return (
      <div className="h-32 flex items-center justify-center text-muted-foreground text-sm">
        No price history available yet
      </div>
    );
  }

  const chartData = history
    .slice()
    .sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
    .map((entry) => ({
      date: format(new Date(entry.timestamp), "MMM d"),
      price: parseFloat(entry.navPrice),
      fullDate: format(new Date(entry.timestamp), "MMM d, yyyy h:mm a"),
    }));

  const firstPrice = chartData.length > 0 ? chartData[0].price : 0;
  const lastPrice = chartData.length > 0 ? chartData[chartData.length - 1].price : 0;
  const priceChange = lastPrice - firstPrice;
  const priceChangePercent = firstPrice > 0 ? (priceChange / firstPrice) * 100 : 0;

  return (
    <div className="space-y-2">
      <div className="flex items-center gap-2">
        {priceChange > 0 ? (
          <>
            <TrendingUp className="h-4 w-4 text-green-600" />
            <span className="text-sm text-green-600">
              +${priceChange.toFixed(2)} (+{priceChangePercent.toFixed(1)}%)
            </span>
          </>
        ) : priceChange < 0 ? (
          <>
            <TrendingDown className="h-4 w-4 text-red-600" />
            <span className="text-sm text-red-600">
              ${priceChange.toFixed(2)} ({priceChangePercent.toFixed(1)}%)
            </span>
          </>
        ) : (
          <>
            <Minus className="h-4 w-4 text-muted-foreground" />
            <span className="text-sm text-muted-foreground">No change</span>
          </>
        )}
        <span className="text-xs text-muted-foreground">since first record</span>
      </div>
      <div className="h-32">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <XAxis
              dataKey="date"
              tick={{ fontSize: 10 }}
              axisLine={false}
              tickLine={false}
            />
            <YAxis
              tick={{ fontSize: 10 }}
              axisLine={false}
              tickLine={false}
              tickFormatter={(value) => `$${value}`}
              width={50}
              domain={["dataMin - 5", "dataMax + 5"]}
            />
            <Tooltip
              formatter={(value: number) => [`$${value.toFixed(2)}`, "NAV Price"]}
              labelFormatter={(_, payload) =>
                payload?.[0]?.payload?.fullDate || ""
              }
              contentStyle={{
                backgroundColor: "hsl(var(--background))",
                border: "1px solid hsl(var(--border))",
                borderRadius: "var(--radius)",
              }}
            />
            <Line
              type="monotone"
              dataKey="price"
              stroke={priceChange >= 0 ? "#16a34a" : "#dc2626"}
              strokeWidth={2}
              dot={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default function MyTokensPage() {
  const { data: portfolio, isLoading } = useQuery<TokenWithAsset[]>({
    queryKey: ["/api/tokens/my-portfolio"],
  });

  if (isLoading) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-10 w-48" />
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <Skeleton key={i} className="h-64" />
          ))}
        </div>
      </div>
    );
  }

  const tokens = portfolio || [];

  const totalValue = tokens.reduce((sum, token) => {
    const price = parseFloat(token.asset.navPrice);
    return sum + token.amount * price;
  }, 0);

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div className="flex items-center gap-4">
          <Link href="/dashboard">
            <Button variant="ghost" size="icon" data-testid="button-back">
              <ArrowLeft className="h-4 w-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-semibold tracking-tight">My Tokens</h1>
            <p className="text-muted-foreground">
              View all your token holdings and historical value
            </p>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="text-sm py-1 px-3">
            Total Value: ${totalValue.toLocaleString("en-US", { minimumFractionDigits: 2 })}
          </Badge>
        </div>
      </div>

      {tokens.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Building2 className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium mb-2">No tokens yet</h3>
            <p className="text-muted-foreground text-center mb-4">
              Start investing by browsing the marketplace
            </p>
            <Link href="/marketplace">
              <Button data-testid="button-browse-marketplace">Browse Marketplace</Button>
            </Link>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {tokens.map((token) => {
            const Icon = assetIcons[token.asset.type];
            const value = token.amount * parseFloat(token.asset.navPrice);
            const pricePerToken = parseFloat(token.asset.navPrice);

            return (
              <Card key={token.id} data-testid={`token-detail-card-${token.id}`}>
                <CardHeader className="pb-2">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex items-center gap-3">
                      <div className="rounded-md bg-muted p-2">
                        <Icon className="h-5 w-5 text-muted-foreground" />
                      </div>
                      <div>
                        <CardTitle className="text-base">{token.asset.title}</CardTitle>
                        <CardDescription className="mt-1">
                          <AssetTypeBadge type={token.asset.type} />
                        </CardDescription>
                      </div>
                    </div>
                    {token.frozen && (
                      <Badge variant="destructive">Frozen</Badge>
                    )}
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="text-xs text-muted-foreground">Tokens Owned</p>
                      <p className="text-lg font-semibold font-mono">
                        {token.amount.toLocaleString()}
                      </p>
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Current Value</p>
                      <p className="text-lg font-semibold font-mono">
                        ${value.toLocaleString("en-US", { minimumFractionDigits: 2 })}
                      </p>
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">NAV Price</p>
                      <p className="font-mono">
                        ${pricePerToken.toFixed(2)}/token
                      </p>
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Total Supply</p>
                      <p className="font-mono">
                        {token.asset.totalSupply.toLocaleString()}
                      </p>
                    </div>
                  </div>

                  <div>
                    <p className="text-xs text-muted-foreground mb-2">Price History</p>
                    <TokenHistoryChart assetId={token.assetId} />
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}

client/src/pages/admin-asset-edit.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useParams, Link } from "wouter";
import { format } from "date-fns";
import {
  ArrowLeft,
  Building2,
  Wheat,
  FileText,
  Coins,
  Save,
  User,
  Edit2,
  X,
  Check,
  History,
  TrendingUp,
  TrendingDown,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { Asset, Token, User as UserType, NavHistory } from "@shared/schema";

type TokenWithOwner = Token & { owner: UserType };

const assetIcons = {
  real_estate: Building2,
  commodity: Wheat,
  loan: FileText,
};

export default function AdminAssetEditPage() {
  const { id } = useParams<{ id: string }>();
  const { toast } = useToast();
  const [navPrice, setNavPrice] = useState("");
  const [reason, setReason] = useState("");
  const [editingTokenId, setEditingTokenId] = useState<string | null>(null);
  const [editAmount, setEditAmount] = useState("");

  const { data: assetData, isLoading } = useQuery<{ asset: Asset; tokens: TokenWithOwner[] }>({
    queryKey: [`/api/admin/assets/${id}/tokens`],
    enabled: !!id,
  });

  const navHistoryKey = id ? `/api/analytics/asset/${id}/nav-history` : null;
  const { data: navHistory } = useQuery<NavHistory[]>({
    queryKey: [navHistoryKey],
    enabled: !!id && !!navHistoryKey,
  });

  const updateNavMutation = useMutation({
    mutationFn: async (data: { navPrice: string; reason: string }) => {
      return await apiRequest("POST", `/api/admin/assets/${id}/nav`, data);
    },
    onSuccess: () => {
      toast({ title: "Token value updated successfully" });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/assets/${id}/tokens`] });
      queryClient.invalidateQueries({ queryKey: ["/api/assets"] });
      queryClient.invalidateQueries({ queryKey: [`/api/analytics/asset/${id}/nav-history`] });
      setNavPrice("");
      setReason("");
    },
    onError: (error: Error) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    },
  });

  const updateTokenMutation = useMutation({
    mutationFn: async (data: { tokenId: string; amount: number; reason: string }) => {
      return await apiRequest("PATCH", `/api/admin/tokens/${data.tokenId}`, { amount: data.amount, reason: data.reason });
    },
    onSuccess: () => {
      toast({ title: "Token amount updated successfully" });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/assets/${id}/tokens`] });
      queryClient.invalidateQueries({ queryKey: ["/api/tokens"] });
      setEditingTokenId(null);
      setEditAmount("");
    },
    onError: (error: Error) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    },
  });

  const handleUpdateNav = () => {
    if (!navPrice || isNaN(parseFloat(navPrice))) {
      toast({ title: "Please enter a valid price", variant: "destructive" });
      return;
    }
    updateNavMutation.mutate({ navPrice, reason: reason || "Admin revaluation" });
  };

  const handleUpdateToken = (tokenId: string) => {
    const amount = parseInt(editAmount);
    if (isNaN(amount) || amount < 0) {
      toast({ title: "Please enter a valid amount", variant: "destructive" });
      return;
    }
    updateTokenMutation.mutate({ tokenId, amount, reason: "Admin adjustment" });
  };

  const startEdit = (token: TokenWithOwner) => {
    setEditingTokenId(token.id);
    setEditAmount(token.amount.toString());
  };

  const cancelEdit = () => {
    setEditingTokenId(null);
    setEditAmount("");
  };

  if (isLoading) {
    return (
      <div className="space-y-8">
        <div className="flex items-center gap-4">
          <Link href="/admin/tokens">
            <Button variant="ghost" size="icon" data-testid="button-back">
              <ArrowLeft className="h-4 w-4" />
            </Button>
          </Link>
          <div className="h-8 w-48 bg-muted animate-pulse rounded" />
        </div>
      </div>
    );
  }

  if (!assetData?.asset) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">Asset not found</p>
        <Link href="/admin/tokens">
          <Button variant="outline" className="mt-4">Back to Assets</Button>
        </Link>
      </div>
    );
  }

  const { asset, tokens } = assetData;
  const AssetIcon = assetIcons[asset.type] || Building2;
  const totalDistributed = asset.totalSupply - asset.remainingSupply;

  return (
    <div className="space-y-8">
      <div className="flex items-center gap-4">
        <Link href="/admin/tokens">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
          </Button>
        </Link>
        <div>
          <h1 className="text-3xl font-semibold tracking-tight flex items-center gap-3">
            <AssetIcon className="h-8 w-8" />
            {asset.title}
          </h1>
          <p className="text-muted-foreground mt-1">
            Edit token value and distribution
          </p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Current Value</CardTitle>
            <CardDescription>Token price per unit</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold" data-testid="text-current-nav">
              ${parseFloat(asset.navPrice).toLocaleString()}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Total Supply</CardTitle>
            <CardDescription>Maximum tokens available</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold" data-testid="text-total-supply">
              {asset.totalSupply.toLocaleString()}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Distribution</CardTitle>
            <CardDescription>Tokens allocated vs remaining</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-3xl font-bold" data-testid="text-distribution">
              {totalDistributed.toLocaleString()} / {asset.totalSupply.toLocaleString()}
            </div>
            <div className="text-sm text-muted-foreground mt-1">
              {asset.remainingSupply.toLocaleString()} remaining
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Coins className="h-5 w-5" />
            Update Token Value
          </CardTitle>
          <CardDescription>
            Change the price per token. This will be reflected in all user portfolios.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <Label htmlFor="navPrice">New Price (USD)</Label>
              <Input
                id="navPrice"
                type="number"
                step="0.01"
                placeholder="e.g., 150.00"
                value={navPrice}
                onChange={(e) => setNavPrice(e.target.value)}
                data-testid="input-nav-price"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="reason">Reason (optional)</Label>
              <Input
                id="reason"
                placeholder="e.g., Quarterly revaluation"
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                data-testid="input-reason"
              />
            </div>
            <div className="flex items-end">
              <Button 
                onClick={handleUpdateNav} 
                disabled={updateNavMutation.isPending}
                className="gap-2 w-full"
                data-testid="button-update-nav"
              >
                <Save className="h-4 w-4" />
                {updateNavMutation.isPending ? "Updating..." : "Update Value"}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader className="flex flex-row items-center justify-between gap-4 space-y-0 pb-4">
          <div>
            <CardTitle className="flex items-center gap-2">
              <History className="h-5 w-5" />
              Token Value History
            </CardTitle>
            <CardDescription>
              Historical record of all token value changes
            </CardDescription>
          </div>
          {navHistory && navHistory.length > 0 && (
            <Badge variant="secondary">{navHistory.length} entries</Badge>
          )}
        </CardHeader>
        <CardContent>
          {!navHistory || navHistory.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <History className="h-8 w-8 mx-auto mb-2 opacity-50" />
              No value history recorded yet.
            </div>
          ) : (
            <div className="rounded-md border max-h-80 overflow-y-auto">
              <Table>
                <TableHeader className="sticky top-0 bg-background">
                  <TableRow>
                    <TableHead>Date</TableHead>
                    <TableHead className="text-right">Price</TableHead>
                    <TableHead>Change</TableHead>
                    <TableHead>Reason</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {navHistory
                    .slice()
                    .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime())
                    .map((entry, idx, arr) => {
                      const currentPrice = parseFloat(entry.navPrice);
                      const prevEntry = arr[idx + 1];
                      const prevPrice = prevEntry ? parseFloat(prevEntry.navPrice) : currentPrice;
                      const change = currentPrice - prevPrice;
                      const changePercent = prevPrice > 0 ? (change / prevPrice) * 100 : 0;

                      return (
                        <TableRow key={entry.id} data-testid={`nav-history-row-${entry.id}`}>
                          <TableCell className="text-sm">
                            {format(new Date(entry.timestamp), "MMM d, yyyy HH:mm")}
                          </TableCell>
                          <TableCell className="text-right font-mono font-medium">
                            ${currentPrice.toFixed(2)}
                          </TableCell>
                          <TableCell>
                            {idx < arr.length - 1 && (
                              <div className="flex items-center gap-1">
                                {change > 0 ? (
                                  <>
                                    <TrendingUp className="h-4 w-4 text-emerald-600" />
                                    <span className="text-sm text-emerald-600">
                                      +${change.toFixed(2)} (+{changePercent.toFixed(1)}%)
                                    </span>
                                  </>
                                ) : change < 0 ? (
                                  <>
                                    <TrendingDown className="h-4 w-4 text-red-600" />
                                    <span className="text-sm text-red-600">
                                      ${change.toFixed(2)} ({changePercent.toFixed(1)}%)
                                    </span>
                                  </>
                                ) : (
                                  <span className="text-sm text-muted-foreground">No change</span>
                                )}
                              </div>
                            )}
                            {idx === arr.length - 1 && (
                              <Badge variant="outline" className="text-xs">Initial</Badge>
                            )}
                          </TableCell>
                          <TableCell className="text-sm text-muted-foreground max-w-48 truncate">
                            {entry.reason || ""}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Token Distribution
          </CardTitle>
          <CardDescription>
            Manage how tokens are distributed among investors
          </CardDescription>
        </CardHeader>
        <CardContent>
          {tokens.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No tokens have been distributed yet.
              <div className="mt-2">
                <Link href="/admin/tokens">
                  <Button variant="outline" size="sm">Go to Token Management</Button>
                </Link>
              </div>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Investor</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead className="text-right">Tokens</TableHead>
                  <TableHead className="text-right">Value</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {tokens.map((token) => (
                  <TableRow key={token.id} data-testid={`row-token-${token.id}`}>
                    <TableCell className="font-medium">
                      <div className="flex items-center gap-2">
                        {token.owner.name}
                        {token.frozen && (
                          <Badge variant="outline" className="text-amber-600">Frozen</Badge>
                        )}
                      </div>
                    </TableCell>
                    <TableCell className="text-muted-foreground">
                      {token.owner.email}
                    </TableCell>
                    <TableCell className="text-right">
                      {editingTokenId === token.id ? (
                        <Input
                          type="number"
                          min="0"
                          value={editAmount}
                          onChange={(e) => setEditAmount(e.target.value)}
                          className="w-24 text-right ml-auto"
                          data-testid="input-edit-amount"
                        />
                      ) : (
                        <span data-testid={`text-amount-${token.id}`}>
                          {token.amount.toLocaleString()}
                        </span>
                      )}
                    </TableCell>
                    <TableCell className="text-right">
                      ${(token.amount * parseFloat(asset.navPrice)).toLocaleString()}
                    </TableCell>
                    <TableCell className="text-right">
                      {editingTokenId === token.id ? (
                        <div className="flex items-center justify-end gap-1">
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => handleUpdateToken(token.id)}
                            disabled={updateTokenMutation.isPending}
                            data-testid="button-confirm-edit"
                          >
                            <Check className="h-4 w-4 text-green-600" />
                          </Button>
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={cancelEdit}
                            data-testid="button-cancel-edit"
                          >
                            <X className="h-4 w-4 text-red-600" />
                          </Button>
                        </div>
                      ) : (
                        <Button
                          size="icon"
                          variant="ghost"
                          onClick={() => startEdit(token)}
                          data-testid={`button-edit-${token.id}`}
                        >
                          <Edit2 className="h-4 w-4" />
                        </Button>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin-user-transactions.content:
import { useQuery } from "@tanstack/react-query";
import { useParams, Link } from "wouter";
import { format } from "date-fns";
import {
  ArrowLeft,
  History,
  ArrowUpRight,
  ArrowDownLeft,
  Building2,
  Wheat,
  FileText,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { TableSkeleton } from "@/components/loading-states";
import type { Transfer, Asset, User } from "@shared/schema";

type TransferWithDetails = Transfer & { asset: Asset; fromUser?: User; toUser?: User };

interface UserTransactionsResponse {
  user: { id: string; name: string; email: string };
  transfers: TransferWithDetails[];
}

const assetIcons = {
  real_estate: Building2,
  commodity: Wheat,
  loan: FileText,
};

const reasonLabels: Record<string, string> = {
  TRADE: "Marketplace Trade",
  MINT: "Token Mint",
  TRANSFER: "Transfer",
  ADMIN_REVOKE: "Admin Revoked",
};

export default function AdminUserTransactionsPage() {
  const params = useParams<{ userId: string }>();
  const userId = params.userId;

  const queryKey = userId ? `/api/admin/users/${userId}/transactions` : null;

  const { data, isLoading } = useQuery<UserTransactionsResponse>({
    queryKey: [queryKey],
    enabled: !!userId && !!queryKey,
  });

  if (isLoading) {
    return (
      <div className="space-y-8">
        <div className="flex items-center gap-4">
          <Link href="/admin/users">
            <Button variant="ghost" size="icon">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div>
            <h1 className="text-3xl font-semibold tracking-tight">User Transaction History</h1>
            <p className="text-muted-foreground mt-1">Loading...</p>
          </div>
        </div>
        <TableSkeleton />
      </div>
    );
  }

  const userInfo = data?.user;
  const transfers = data?.transfers || [];

  const getTransactionType = (transfer: TransferWithDetails) => {
    if (transfer.toUserId === userId) {
      return "buy";
    } else if (transfer.fromUserId === userId) {
      return "sell";
    }
    return "transfer";
  };

  return (
    <div className="space-y-8">
      <div className="flex items-center gap-4">
        <Link href="/admin/users">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-5 w-5" />
          </Button>
        </Link>
        <div>
          <h1 className="text-3xl font-semibold tracking-tight">
            Transaction History
          </h1>
          <p className="text-muted-foreground mt-1">
            {userInfo?.name} ({userInfo?.email})
          </p>
        </div>
      </div>

      <Card>
        <CardHeader className="flex flex-row items-center justify-between gap-4 space-y-0 pb-4">
          <div>
            <CardTitle className="text-lg">All Transactions</CardTitle>
            <CardDescription>Complete buy and sell history for this user</CardDescription>
          </div>
          <Badge variant="secondary">{transfers.length} transactions</Badge>
        </CardHeader>
        <CardContent>
          {transfers.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12 text-center">
              <div className="rounded-full bg-muted p-4 mb-4">
                <History className="h-8 w-8 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-semibold mb-1">No transactions</h3>
              <p className="text-sm text-muted-foreground max-w-sm">
                This user has no transaction history yet.
              </p>
            </div>
          ) : (
            <div className="rounded-md border">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Type</TableHead>
                    <TableHead>Asset</TableHead>
                    <TableHead>Reason</TableHead>
                    <TableHead className="text-right">Amount</TableHead>
                    <TableHead>Counterparty</TableHead>
                    <TableHead>Date</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {transfers.map((transfer) => {
                    const Icon = assetIcons[transfer.asset.type];
                    const txType = getTransactionType(transfer);
                    const isBuy = txType === "buy";

                    const counterparty = isBuy
                      ? transfer.fromUser?.name || "System"
                      : transfer.toUser?.name || "System";

                    return (
                      <TableRow key={transfer.id} data-testid={`transaction-row-${transfer.id}`}>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            <div className={`rounded-md p-1.5 ${isBuy ? "bg-emerald-100 dark:bg-emerald-950" : "bg-red-100 dark:bg-red-950"}`}>
                              {isBuy ? (
                                <ArrowDownLeft className={`h-4 w-4 text-emerald-600 dark:text-emerald-400`} />
                              ) : (
                                <ArrowUpRight className="h-4 w-4 text-red-600 dark:text-red-400" />
                              )}
                            </div>
                            <Badge 
                              variant="outline" 
                              className={isBuy ? "border-emerald-500/50 text-emerald-600 dark:text-emerald-400" : "border-red-500/50 text-red-600 dark:text-red-400"}
                            >
                              {isBuy ? "Buy" : "Sell"}
                            </Badge>
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            <Icon className="h-4 w-4 text-muted-foreground" />
                            <span className="font-medium">{transfer.asset.title}</span>
                          </div>
                        </TableCell>
                        <TableCell>
                          <span className="text-sm text-muted-foreground">
                            {reasonLabels[transfer.reason] || transfer.reason}
                          </span>
                        </TableCell>
                        <TableCell className="text-right">
                          <span className={`font-mono font-semibold ${isBuy ? "text-emerald-600 dark:text-emerald-400" : "text-red-600 dark:text-red-400"}`}>
                            {isBuy ? "+" : "-"}{transfer.tokenAmount.toLocaleString()}
                          </span>
                        </TableCell>
                        <TableCell>
                          <span className="text-sm text-muted-foreground">{counterparty}</span>
                        </TableCell>
                        <TableCell>
                          <span className="text-sm text-muted-foreground">
                            {format(new Date(transfer.timestamp), "MMM d, yyyy HH:mm")}
                          </span>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}
